sourcesContent:
import express from 'express';
import routes from './routes';

const app = express();
app.use(express.json({ limit: '200Mb' }));
const PORT = process.env.PORT || 5000;

routes(app);

app.listen(PORT, () => {
  console.log(`Server running on port ${PORT}`);
});

export default app;
module.exports = app;

